mapper.select:
select seq, menuCode, menuTitle, menuURL
 from TB_MENU
 where seq=#{seq}

select seq, menuCode, menuTitle, menuURL
 from
	(
		select seq, menuCode, menuTitle, menuURL
		from TB_MENU


				and seq = #{seq}


				and menuTitle = #{menuTitle}


				and menuCode = #{menuCode}



	) TB_MENU
	order by seq desc

select count(*) totcnt
 from TB_MENU


		and menuTitle like #{searchKeyword}

mapper.update:
		update TB_MENU set
			menuCode=#{menuCode},
 		   	menuTitle=#{menuTitle},
  		  	menuURL=#{menuURL}
		where seq=#{seq}
